// blockchain/contracts/EvidenceChain.sol
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

contract EvidenceChain {
    struct Evidence {
        uint256 id;
        string ipfsHash;
        string description;
        uint256 timestamp;
        address owner;
        bool exists;
    }

    mapping(uint256 => Evidence) public evidenceRecords;
    uint256 public totalEvidence;
    
    event EvidenceAdded(
        uint256 indexed id,
        string ipfsHash,
        address indexed owner,
        uint256 timestamp
    );

    constructor() {
        totalEvidence = 0;
    }

    function addEvidence(string memory _ipfsHash, string memory _description) 
        public 
        returns (uint256) 
    {
        require(bytes(_ipfsHash).length > 0, "IPFS hash cannot be empty");
        
        totalEvidence++;
        uint256 newId = totalEvidence;
        
        evidenceRecords[newId] = Evidence({
            id: newId,
            ipfsHash: _ipfsHash,
            description: _description,
            timestamp: block.timestamp,
            owner: msg.sender,
            exists: true
        });

        emit EvidenceAdded(newId, _ipfsHash, msg.sender, block.timestamp);
        return newId;
    }

    function getEvidence(uint256 _id) 
        public 
        view 
        returns (Evidence memory) 
    {
        require(evidenceRecords[_id].exists, "Evidence does not exist");
        return evidenceRecords[_id];
    }

    function getEvidenceCount() public view returns (uint256) {
        return totalEvidence;
    }
}
